/* General container */
.web-form-container {
  background-color: #ffffff;
  border-radius: 16px;
  padding: 40px;
  max-width: 700px;
  margin: 40px auto;
  font-family: "Segoe UI", "Helvetica Neue", sans-serif;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.06);
}

/* Headings */
.web-form-container h1,
.web-form-container h2,
.web-form-container h3 {
  color: #eb1c24;
  text-align: center;
  margin-bottom: 20px;
}

/* Field labels */
.control-label {
  font-weight: 600;
  color: #002147;
  margin-bottom: 6px;
  display: block;
  font-size: 15px;
}

/* Input fields */
.form-control {
  background-color: #f9f9f9;
  border: none;
  border-radius: 10px;
  padding: 12px 16px;
  font-size: 15px;
  box-shadow: inset 0 0 0 1px #ddd;
  transition: box-shadow 0.2s ease-in-out;
  margin-bottom: 25px;
}

.form-control:focus {
  box-shadow: 0 0 0 2px #eb1c24;
  outline: none;
}

/* Section breaks (optional titles) */
.form-section {
  font-size: 18px;
  font-weight: 600;
  color: #002147;
  margin-top: 30px;
  margin-bottom: 10px;
}

/* Submit Button */
.btn-primary {
  background-color: #eb1c24;
  border: none;
  padding: 12px 30px;
  font-size: 16px;
  border-radius: 10px;
  color: #fff;
  font-weight: 600;
  transition: background-color 0.2s ease-in-out;
}

.btn-primary:hover {
  background-color: #c3141c;
}

/* Remove default border from file input */
input[type="file"] {
  border: none !important;
  background-color: transparent !important;
  padding: 0;
}

/* Responsive */
@media (max-width: 768px) {
  .web-form-container {
    padding: 20px;
  }
}
